import React, {Component} from 'react';
// import HospitalPersonnelService from "./HospitalPersonnelService";
import {useNavigate} from "react-router";

class CreateHospitalPerson extends Component {
    constructor(props) {
        super(props);

        this.state = {
            input: {},
            msg: {},
        }

        this.saveChangeHandler = this.saveChangeHandler.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        // this.saveHospitalPerson = this.saveHospitalPerson.bind(this);

    }

    saveHospitalPerson = (e) => {
        e.preventDefault();
        let hospitalPersonnel = {name: this.state.input.name, email: this.state.input.email,
                                    numPager: this.state.input.numPager, password: this.state.input.password,
                                        role: this.state.input.role};
        console.log('Hospital Personnel =>' + JSON.stringify(hospitalPersonnel))

        // HospitalPersonnelService.createHospitalPerson(hospitalPersonnel).then(res => {
        //     this.props.history.push('persons')
        // })
    }

    saveChangeHandler(e) {
        const {target} = e.target;
        var save = this.state.input;
        save[target.name] = target.value;
        this.setState({input: save});
    }

    validation(e) {
        var val = {};
        if (this.state.input["password"]!==this.state.input["confirmPassword"]) {
            val["password"] = "Passwords do not match.";
        }
        else {
            val["confirmPassword"] = "";
        }
        this.setState({msg:val})
    }

    handleSubmit(e) {
        e.preventDefault(); // Prevent clicking submit with empty args
        if(this.validation()) {
            this.saveHospitalPerson();
            var input = {};
        }
    }

    render() {
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <label>
                            <b>Name:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="name"type="text" required
                               value={this.state.input.name} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Pager Number:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="numPager" type="text" required value={this.state.input.numPager} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Email:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="email" type="email" required
                               value={this.state.input.email} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Password:</b>
                        </label>
                        {/*// TODO: Add logic here to confirm passwords match*/}
                        <input className = "form-control" type="inputSignup" name="password" type="password" required
                               value={this.state.input.password} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Confirm Password:</b>
                        </label>
                        {/*// TODO: Add logic here to confirm passwords match*/}
                        <input className = "form-control" type="inputSignup" name="confirmPassword" type="password" required
                               value={this.state.input.confirmPassword} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label htmlFor="role">
                            <b>Role:</b>
                        </label>
                        <select name="role" type="role" id="role" required>
                            <option value="empty" onChange={this.saveChangeHandler}></option>
                            <option value="JuniorDoctor" onChange={this.saveChangeHandler}>FY</option>
                            <option value="SHO" onChange={this.saveChangeHandler}>SHO</option>
                            <option value="Consultant" onChange={this.saveChangeHandler}>Spr/Cons</option>
                        </select>
                        <div>
                            <strong>
                                <div className="text-danger" id = "passwordCheck">
                                    {this.state.msg.password}
                                </div>
                            </strong>
                        </div>
                        <div id="actionButtonsSignUp">
                            <button id="actionButtonSignUp1" onClick={null}>
                                Return to login page
                            </button>
                            <button id="actionButtonSignUp2" onClick={this.handleSubmit}>Register</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}


export default CreateHospitalPerson;
