import React, {Component} from 'react';
import './Login.css';

class LoginDoctor extends Component {
    constructor(props) {
        super(props);

        this.state = {
            email: '',
            password: '',
            msg: '',
        }

        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.changePasswordHandler = this.changePasswordHandler.bind(this);
    }

    changeEmailHandler=(event) => {
        this.setState({email: event.target.value});
    }

    changePasswordHandler=(event) => {
        this.setState({password: event.target.value});
    }

    validation(e) {
        // TODO: Check if doctor email in dataabase
        // if (this.state.password!==this.state.confirmPassword) {
        //     this.setState({msg:"Passwords do not match."});
        //     return false;
        // }
        return true;
    }

    checkEntries(e) {
        if (this.state.email==="" || this.state.password==="") {
            this.setState({msg: "Please fill out all entries."});
            return false;
        }
        return true;
    }

    handleSubmit(e) {
        e.preventDefault(); // Prevent clicking submit with empty args
        if(this.checkEntries()) {
            if(this.validation()) {
                this.saveHospitalPerson();
                this.props.register();
            }
        }
    }

    render() {
        return (
            <div id="mainBoxL">
                <div className="row">
                    <label>
                        <b>NHS Email:</b>
                    </label>
                    <input className = "form-control" type="email" onChange={this.changeEmailHandler}/>
                </div>

                <div className="row">
                    <label>
                        <b>Password:</b>
                    </label>
                    <input className = "form-control" type="password" onChange={this.changeEmailHandler}></input>
                </div>
                <div>
                    <strong>
                        <div className="text-danger">
                            {this.state.msg}
                        </div>
                    </strong>
                </div>
                <div id="actionButtonsLogin">
                    <button id="button1" onClick={this.props.signUp}>
                        Sign up
                    </button>

                    <button id="button2"  onClick={this.props.forgotPassword}>
                        Forgot Password
                    </button>

                    <button id="button3" onClick={this.handleSubmit}  >
                        Login
                    </button>
                    </div>
            </div>
        );
    }
}

export default LoginDoctor;
