import React, {Component} from 'react';
// import HospitalPersonnelService from "./HospitalPersonnelService";
import {useNavigate} from "react-router";

class CreateHospitalPerson extends Component {
    constructor(props) {
        super(props);

        this.state = {
            input: {},
            msg: {},
            name: '',
            email: '',
            numPager: '',
            password: '',
            confirmPassword: '',
            role: '',
            msg: ''
        }
        this.changeNameHandler = this.changeNameHandler.bind(this);
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.changeNumPagerHandler = this.changeNumPagerHandler.bind(this);
        this.changePasswordHandler = this.changePasswordHandler.bind(this);
        this.changeConfirmPasswordHandler = this.changeConfirmPasswordHandler.bind(this);
        this.changeRoleHandler = this.changeRoleHandler.bind(this);

        this.pwdConfirm = this.pwdConfirm.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.saveHospitalPerson = this.saveHospitalPerson.bind(this);

    }

    saveHospitalPerson = (e) => {
        e.preventDefault();
        let hospitalPersonnel = {name: this.state.name, email: this.state.email, numPager: this.state.numPager, password: this.state.password, role: this.state.role};
        console.log('Hospital Personnel =>' + JSON.stringify(hospitalPersonnel))

        // HospitalPersonnelService.createHospitalPerson(hospitalPersonnel).then(res => {
        //     this.props.history.push('persons')
        // })
    }

    changeNameHandler=(event) => {
        this.setState({name: event.target.value});
    }
    changeNumPagerHandler=(event) => {
        this.setState({numPager: event.target.value});
    }
    changeEmailHandler=(event) => {
        this.setState({email: event.target.value});
    }
    changePasswordHandler=(event) => {
        this.setState({password: event.target.value});
    }
    changeConfirmPasswordHandler=(event) => {
        this.setState({confirmPassword: event.target.value});
    }
    changeRoleHandler=(event) => {
        this.setState({role: event.target.value});
    }

    pwdConfirm(e) {
        var inputpwd = this.state.input;
        inputpwd[e.target.name] = e.target.value;
        this.setState({inputpwd});
    }

    validation(e) {
        var val = {};
        if (this.state.input["password"]!==this.state.input["confirmPassword"]) {
            val["password"] = "Passwords do not match.";
        }
        else {
            val["confirmPassword"] = "";
        }
        this.setState({msg:val})
    }

    handleSubmit(e) {
        e.preventDefault(); // Prevent clicking submit with empty args
        if(this.validation()) {
            var input = {};
            input["password"] = "";
            input["confirmPassword"] = "";
        }
    }

    render() {
        return (
            <div className="container">
                <form onSubmit={this.handleSubmit}>
                    <div className="row">
                        <label>
                            <b>Name:</b>
                        </label>
                        <input type="inputSignup" name="name"type="text" required
                               value={this.state.name} onChange={this.changeNameHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Pager Number:</b>
                        </label>
                        <input type="inputSignup" name="numPager" type="text" required value={this.state.numPager} onChange={this.changeNumPagerHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Email:</b>
                        </label>
                        <input type="inputSignup" name="email" type="email" required
                               value={this.state.email} onChange={this.changeEmailHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Password:</b>
                        </label>
                        {/*// TODO: Add logic here to confirm passwords match*/}
                        <input class = "form-control" type="inputSignup" name="password" type="password" required
                               value={this.state.input.password} onChange={this.pwdConfirm}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Confirm Password:</b>
                        </label>
                        {/*// TODO: Add logic here to confirm passwords match*/}
                        <input class = "form-control" type="inputSignup" name="confirmPassword" type="password" required
                               value={this.state.input.confirmPassword} onChange={this.pwdConfirm}/>
                    </div>
                    <div className="row">
                        <label htmlFor="role">
                            <b>Role:</b>
                        </label>
                        <select name="role" name="role" id="role">
                            <option value="empty" onChange={this.changeRoleHandler}></option>
                            <option value="JuniorDoctor" onChange={this.changeRoleHandler}>FY</option>
                            <option value="SHO" onChange={this.changeRoleHandler}>SHO</option>
                            <option value="Consultant" onChange={this.changeRoleHandler}>Spr/Cons</option>
                        </select>
                        <div style="marginTop = 25px">
                            <strong>
                                <div className="text-danger">
                                    {this.state.msg.password}
                                </div>
                            </strong>
                        </div>
                        <div id="actionButtonsSignUp">
                            <button id="actionButtonSignUp" onClick={null}>
                                Return to login page
                            </button>
                            <button id="actionButtonSignUp" onClick={this.handleSubmit}>Register</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}


export default CreateHospitalPerson;
