import React, {Component} from 'react';
import {MemoryRouter, useHistory} from "react-router"
// import HospitalPersonnelService from "./HospitalPersonnelService";
import {useNavigate} from "react-router";

class CreateHospitalPerson extends Component {
    constructor(props) {
        super(props);

        this.state = {
            input: {},
            name: '',
            email: '',
            numPager: '',
            password: '',
            confirmPassword: '',
            role: '',
            msg: '',
        }

        // this.saveChangeHandler = this.saveChangeHandler.bind(this);
        this.changeNameHandler = this.changeNameHandler.bind(this);
        this.changeEmailHandler = this.changeEmailHandler.bind(this);
        this.changeNumPagerHandler = this.changeNumPagerHandler.bind(this);
        this.changePasswordHandler = this.changePasswordHandler.bind(this);
        this.changeConfirmPasswordHandler = this.changeConfirmPasswordHandler.bind(this);
        this.changeRoleHandler = this.changeRoleHandler.bind(this);
        this.saveRoleChangeHandler = this.saveRoleChangeHandler.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    saveHospitalPerson = (e) => {
        let hospitalPersonnel = {name: this.state.input.name, email: this.state.input.email,
                                    numPager: this.state.input.numPager, password: this.state.input.password,
                                        role: this.state.role};
        console.log('Hospital Personnel =>' + JSON.stringify(hospitalPersonnel))

        this.setState({msg: JSON.stringify(hospitalPersonnel)})

        // HospitalPersonnelService.createHospitalPerson(hospitalPersonnel).then(res => {
        // })
    }

    // saveChangeHandler(e) {
    //     const {name, value} = e.target;
    //     var save = this.state.input;
    //     save[name] = value;
    //     this.setState({input: save});
    // }

    changeNameHandler=(event) => {
        this.setState({name: event.target.value});
    }

    changeEmailHandler=(event) => {
        this.setState({email: event.target.value});
    }
    changeNumPagerHandler=(event) => {
        this.setState({numPager: event.target.value});
    }
    changePasswordHandler=(event) => {
        this.setState({password: event.target.value});
    }
    changeConfirmPasswordHandler=(event) => {
        this.setState({confirmPassword: event.target.value});
    }
    changeRoleHandler=(event) => {
        this.setState({role: event.target.value});
    }

    validation(e) {
        if (this.state.password!==this.state.confirmPassword) {
            this.setState({msg:"Passwords do not match."})
        }
    }

    handleSubmit(e) {
        e.preventDefault(); // Prevent clicking submit with empty args
        if(this.validation()) {
            var input = {};
        }
        else {
            this.saveHospitalPerson();
        }
    }

    render() {
        return (
            <div className="container">
                {/*<form>*/}
                    <div className="row">
                        <label>
                            <b>Name:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="name"type="text" required
                               value={this.state.input.name} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Pager Number:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="numPager" type="text" required
                               value={this.state.input.numPager} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Email:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="email" type="email" required
                               value={this.state.input.email} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Password:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="password" type="password" required
                               value={this.state.input.password} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label>
                            <b>Confirm Password:</b>
                        </label>
                        <input className = "form-control" type="inputSignup" name="confirmPassword" type="password"
                               required value={this.state.input.confirmPassword} onChange={this.saveChangeHandler}/>
                    </div>
                    <div className="row">
                        <label htmlFor="role">
                            <b>Role:</b>
                        </label>
                        <select name="role" type="role" id="role" required>
                            <option value="empty" onChange={this.saveRoleChangeHandler}></option>
                            <option value="JuniorDoctor" onChange={this.saveRoleChangeHandler}>FY</option>
                            <option value="SHO" onChange={this.saveRoleChangeHandler}>SHO</option>
                            <option value="Consultant" onChange={this.saveRoleChangeHandler}>Spr/Cons</option>
                        </select>
                        <div>
                            <strong>
                                <div className="text-danger" id = "passwordCheck">
                                    {this.state.msg}
                                </div>
                            </strong>
                        </div>
                        <div id="actionButtonsSignUp">
                            <button id="actionButtonSignUp1" onClick={this.props.home}>
                                Return to login page
                            </button>
                            <button id="actionButtonSignUp2" onClick={this.handleSubmit}>Register</button>
                        </div>
                    </div>
                {/*</form>*/}
            </div>
        );
    }
}

export default CreateHospitalPerson;
